# æ£€æŸ¥å¹¶é‡Šæ”¾ 80 ç«¯å£ (ç”¨äº ACME Standalone æ¨¡å¼)
release_80_port() {
    log "æ­£åœ¨æ£€æŸ¥ 80 ç«¯å£å ç”¨..."
    local pid=$(lsof -t -i:80)
    if [ -n "$pid" ]; then
        yellow "è­¦å‘Š: 80 ç«¯å£è¢«è¿›ç¨‹ $pid å ç”¨ã€‚"
        read -p "æ˜¯å¦å°è¯•å¼ºè¡Œé‡Šæ”¾ 80 ç«¯å£? (é»˜è®¤â€œå¦â€) [Y/n]: " kill_confirm
        kill_confirm=${kill_confirm:-n}
        if [[ "$kill_confirm" == "y" || "$kill_confirm" == "Y" ]]; then
            log "æ­£åœ¨å¼ºè¡Œé‡Šæ”¾ 80 ç«¯å£..."
            kill -9 $pid
            sleep 2
            if lsof -t -i:80 >/dev/null; then
                red "80 ç«¯å£é‡Šæ”¾å¤±è´¥ï¼"
                return 1
            else
                green "80 ç«¯å£å·²é‡Šæ”¾ã€‚"
            fi
        else
            red "80 ç«¯å£è¢«å ç”¨ï¼ŒACME Standalone æ¨¡å¼æ— æ³•ç»§ç»­ã€‚"
            return 1
        fi
    fi
    return 0
}

# è·å–æˆ–ç»­è®¢è¯ä¹¦ (æ”¯æŒ DNS API å’Œ Standalone)
# --- JQ Patch Helpers ---
ensure_jq() {
    if ! command -v jq &>/dev/null; then
        echo "Installing jq..."
        if command -v apt-get &>/dev/null; then apt-get update && apt-get install -y jq
        elif command -v yum &>/dev/null; then yum install -y jq
        elif command -v dnf &>/dev/null; then dnf install -y jq
        else echo "Error: Cannot install jq automatically."; return 1; fi
    fi
}

generate_json_config() {
    local output_file=$1; local filter=$2; shift 2; local args=("$@")
    if ! jq -n "${args[@]}" "$filter" > "$output_file"; then
        echo -e "\033[0;31mError generating JSON config.\033[0m"; return 1
    fi
    chmod 644 "$output_file"
}

ax_get_certificate() {
    local domain=$1
    local cert_dir="$AX_CERT_DIR/$domain"
    if [ -f "$cert_dir/fullchain.cer" ]; then log "è¯ä¹¦ $domain å·²å­˜åœ¨"; return 0; fi
    install_acme_sh_client || return 1
    ensure_jq
    
    log "éªŒè¯ DNS è§£æ..."
    local v4=$(curl -s4m2 $PUBLIC_IP_SERVICE_1); local v6=$(curl -s6m2 $PUBLIC_IP_SERVICE_1)
    # DNS check logic simplified for patch, assuming user ensures DNS
    
    read -p "é€‰æ‹©éªŒè¯æ¨¡å¼ [1]80ç«¯å£ [2]DNS API: " acme_mode
    local acme_cmd=("$ACME_SH_INSTALL_DIR" "--issue" "-d" "$domain" "-k" "ec-256")
    
    if [ "$acme_mode" == "1" ]; then
        release_80_port || return 1
        acme_cmd+=("--standalone")
        [[ -n "$v6" ]] && acme_cmd+=("--listen-v6")
    elif [ "$acme_mode" == "2" ]; then
        read -p "DNSå•† [1]CF [2]DNSPod [3]Aliyun: " p
        case $p in
            1) read -s -p "CF Key: " k; echo; read -p "CF Email: " e; export CF_Key="$k" CF_Email="$e"; acme_cmd+=("--dns" "dns_cf");;
            2) read -s -p "ID: " i; echo; read -s -p "Key: " k; echo; export DP_Id="$i" DP_Key="$k"; acme_cmd+=("--dns" "dns_dp");;
            3) read -s -p "Key: " k; echo; read -s -p "Secret: " s; echo; export Ali_Key="$k" Ali_Secret="$s"; acme_cmd+=("--dns" "dns_ali");;
            *) red "æ— æ•ˆé€‰é¡¹"; return 1;;
        esac
    else red "æ— æ•ˆæ¨¡å¼"; return 1; fi

    log "æ‰§è¡Œç”³è¯·..."
    "${acme_cmd[@]}"
    if [ $? -eq 0 ]; then
        mkdir -p "$cert_dir"
        "$ACME_SH_INSTALL_DIR" --install-cert -d "$domain" --ecc --fullchain-file "$cert_dir/fullchain.cer" --key-file "$cert_dir/private.key"
        green "è¯ä¹¦ç”³è¯·æˆåŠŸ"; "$ACME_SH_INSTALL_DIR" --cron -f >/dev/null 2>&1; return 0
    else red "ç”³è¯·å¤±è´¥"; return 1; fi
}


