            port=$(jq -r '.listen // .inbounds[0].port // "N/A"' "$conf" 2>/dev/null)
        elif [[ "$conf" == *".conf" ]]; then port=$(grep -Po '(?<=-l )[^ ]+' "$conf"); fi
        echo "çŠ¶æ€: $st (ç«¯å£: $port)"
        echo "-----------------"
        echo "1) é‡å¯"; echo "2) åœæ­¢"; echo "3) æŸ¥çœ‹é…ç½®/é“¾æ¥"; echo "4) ç¼–è¾‘é…ç½®"; echo "5) æ—¥å¿—"; echo "99) åˆ é™¤"; echo "0) è¿”å›"
        read -p "Select: " c
        case $c in
            1) systemctl restart "$service"; green "å·²é‡å¯";;
