            99) read -p "ç¡®è®¤? [y/N] " y; if [[ "$y" == "y" ]]; then systemctl stop "$service"; systemctl disable "$service"; rm -f "$conf"; systemctl daemon-reload; green "å·²åˆ é™¤"; return; fi;;
            0) return;;
        esac
        sleep 0.5
    done
}


# --- JQ å®‰å…¨è¾…åŠ©å‡½æ•° ---
escape_json_val() {
    local val="$1"
    if command -v jq >/dev/null; then
        echo -n "$val" | jq -R -s '.' | sed 's/^"//;s/"$//'
    else
        echo -n "$val"
    fi
}
# åˆ›å»ºä¸€ä¸ªæ–°çš„ç‹¬ç«‹å®ä¾‹ (hy2, udp2raw, kcptun)
create_new_instance() {
    local type=$1
