    [[ -z "$listen_port" ]] && listen_port=$(find_available_port)
    green "ä½¿ç”¨ç«¯å£: $listen_port"
    
    case "$type" in
        hysteria2)
            service_name="ax-hysteria2@${next_id}.service"
            conf_path="$HY2_INSTALL_DIR/hy2_${next_id}.json"
            
            local use_acme=$(read_input "ä½¿ç”¨ ACME è¯ä¹¦? [Y/n]" "y")
            local cert="" key=""
            if [[ "$use_acme" =~ ^[yY] ]]; then
                local domain=$(read_input "åŸŸå" ""); ax_get_certificate "$domain" || return 1
                cert="$AX_CERT_DIR/$domain/fullchain.cer"; key="$AX_CERT_DIR/$domain/private.key"
            else
