            local ignore_bw=$(read_input "å¿½ç•¥å¸¦å®½æç¤º? [y/N]" "y"); local ign="false"; [[ "$ignore_bw" =~ ^[yY] ]] && ign="true"
            
            local warp_obj="{}"
