            if [[ "$en_warp" =~ ^[yY] ]]; then
                local wp=$(read_input "WARPç«¯å£" "$DEFAULT_WARP_SOCKS_PORT")
                warp_obj=$(jq -n --arg addr "127.0.0.1:$wp" '{outbounds: [{name: "warp", type: "socks5", socks5: {addr: $addr, udp: false}}], acl: {inline: ["warp(geosite:google)", "direct(all)"]}}')
            else warp_obj='{"acl": {"inline": ["direct(all)"]}}'; fi

            local filter='{listen: (":"+$port), tls: {cert: $cert, key: $key}, auth: {type: "password", password: $pass}, masquerade: {type: "proxy", proxy: {url: "https://www.bing.com", rewriteHost: true}}, ignoreClientBandwidth: ($ign|test("true")), sniff: {enable: true, timeout: "2s", rewriteDomain: true, tcpPorts: "80,443", udpPorts: "all"}} + $warp'
            generate_json_config "$conf_path" "$filter" --arg port "$listen_port" --arg cert "$cert" --arg key "$key" --arg pass "$password" --arg ign "$ign" --argjson warp "$warp_obj"
            ;;
        kcptun)
