            local t_addr=$(read_input "ç›®æ ‡åœ°å€" "$DEFAULT_TARGET_ADDR")
            local t_port=$(read_input "ç›®æ ‡ç«¯å£" ""); [[ -z "$t_port" ]] && t_port=$(find_available_port)
            local key=$(handle_password_input "kcptun")
            local filter='{listen: (":"+$port), target: $target, key: $key, crypt: "aes-128", mode: "fast3", mtu: 1350, sndwnd: 512, rcvwnd: 512, datashard: 10, parityshard: 3, dscp: 46, nocomp: true, tcp: false}'
            generate_json_config "$conf_path" "$filter" --arg port "$listen_port" --arg target "${t_addr}:${t_port}" --arg key "$key"
            ;;
        udp2raw)
            service_name="ax-udp2raw@${next_id}.service"
            conf_path="$UDP2RAW_INSTALL_DIR/udp2raw_${next_id}.conf"
            local target=$(read_input "ç›®æ ‡åœ°å€" "127.0.0.1:8080")
            local pass=$(handle_password_input "udp2raw")
            echo -e "-s\n-l 0.0.0.0:${listen_port}\n-r ${target}\n-k ${pass}\n--raw-mode faketcp\n--cipher-mode aes128cbc\n--auth-mode hmac_sha1\n-a" > "$conf_path"
            ;;
    esac
    if [[ -f "$conf_path" ]]; then
        systemctl enable --now "$service_name"
        systemctl is-active --quiet "$service_name" && green "å®ä¾‹åˆ›å»ºæˆåŠŸ ID: $next_id" || red "å¯åŠ¨å¤±è´¥"
    fi
}

