# --- [Refactored] Xray Instance Creator (JQ Version) ---
create_new_xray_instance() {
    local type=$1
    local next_id=$(find_next_available_id "xray")
    local conf_path="${XRAY_INSTALL_DIR}/xray_${next_id}.json"
    local log_level="warning"
    
    echo
    log "å¯åŠ¨ä¸€ä¸ªæ–°çš„ Xray å®ä¾‹ (ID: ${next_id}) - JQ å®‰å…¨æ„å»ºæ¨¡å¼"
    
    # --- 1. åŸºç¡€äº¤äº’ ---
    local listen_addr=$(read_input "è¯·è¾“å…¥ç›‘å¬åœ°å€" "$DEFAULT_LISTEN_ADDR")
    local listen_port=$(read_input "è¯·è¾“å…¥ç›‘å¬ç«¯å£ (ç•™ç©ºåˆ™éšæœº)" "")
    if [[ -z "$listen_port" ]]; then listen_port=$(find_available_port); green "éšæœºç«¯å£: $listen_port"; fi
    
    # å‡†å¤‡ JQ å‚æ•°åˆ—è¡¨
    local jq_args=(
        --arg port "$listen_port"
        --arg listen "$listen_addr"
        --arg log "$log_level"
    )

    # --- 2. åè®®ç‰¹å®šäº¤äº’ ---
    local protocol_settings="{}"
    local stream_settings="{}"
    local uuid=""
    
    case "$type" in
        xray_reality)
            uuid=$(generate_strong_password)
            local sni=$(read_input "SNI (ç›®æ ‡ç½‘ç«™)" "$XRAY_REALITY_DEFAULT_SNI")
            
            # å¯†é’¥ç”Ÿæˆ
            local key_pair=$("$XRAY_INSTALL_DIR/xray" x25519)
            local private_key=$(echo "$key_pair" | grep -i "Private" | awk -F':' '{print $2}' | head -n1 | tr -d '[:space:]')
            local public_key=$(echo "$key_pair" | grep -i "Public" | awk -F':' '{print $2}' | head -n1 | tr -d '[:space:]')
            # å…¼å®¹æ€§ fallback
            if [[ -z "$public_key" ]]; then public_key=$(echo "$key_pair" | grep -i "Password" | awk -F':' '{print $2}' | head -n1 | tr -d '[:space:]'); fi
            
            if [[ -z "$private_key" ]]; then red "é”™è¯¯ï¼šæ— æ³•ç”Ÿæˆ Xray å¯†é’¥"; return 1; fi
            
            local short_id=$(openssl rand -hex 8)
            
            jq_args+=(
                --arg uuid "$uuid"
                --arg sni "$sni"
                --arg pk "$private_key" 
                --arg pbk "$public_key"
                --arg sid "$short_id"
                --arg fp "$XRAY_REALITY_DEFAULT_FP"
                --arg target "${sni}:443"
            )
            ;;
            
        xray_mkcp)
            uuid=$(generate_strong_password)
            local seed=$(handle_password_input "xray_mkcp")
            
            local mtu=$(read_input "MTU" "1350")
            local tti=$(read_input "TTI" "20")
            local uplink=$(read_input "Uplink (MB/s)" "5")
            local downlink=$(read_input "Downlink (MB/s)" "20")
            local congestion=$(read_input_with_options "å¯ç”¨æ‹¥å¡æ§åˆ¶?" "1" "æ˜¯ (true)" "å¦ (false)")
            local is_congestion="false"; [[ "$congestion" == "1" ]] && is_congestion="true"
            
            local read_buf=$(read_input "Read Buffer (MB)" "2")
            local write_buf=$(read_input "Write Buffer (MB)" "2")
            
            local header_opts=("none" "srtp" "utp" "wechat-video" "dtls" "wireguard")
            local header_idx=$(read_input_with_options "ä¼ªè£…å¤´ç±»å‹:" "4" "${header_opts[@]}")
            local header_type="${header_opts[$((header_idx-1))]}"
            
            jq_args+=(
                --arg uuid "$uuid"
                --arg seed "$seed"
                --argjson mtu "$mtu"
                --argjson tti "$tti"
                --argjson up "$uplink"
                --argjson down "$downlink"
                --argjson cong "$is_congestion"
                --argjson rb "$read_buf"
                --argjson wb "$write_buf"
                --arg header "$header_type"
            )
            ;;
            
        xray_ss)
            local ss_opts=("2022-blake3-aes-128-gcm" "2022-blake3-aes-256-gcm" "2022-blake3-chacha20-poly1305" "aes-128-gcm" "aes-256-gcm" "chacha20-ietf-poly1305")
            local ss_idx=$(read_input_with_options "åŠ å¯†æ–¹å¼:" "2" "${ss_opts[@]}")
            local method="${ss_opts[$((ss_idx-1))]}"
            local password=$(handle_password_input "shadowsocks" "$method")
            
            jq_args+=(
                --arg method "$method"
                --arg password "$password"
            )
            ;;
    esac
    
    # --- 3. Warp åˆ†æµäº¤äº’ ---
    local enable_warp=$(read_input "å¯ç”¨ WARP SOCKS5 åˆ†æµ? [y/N]" "n")
    local warp_obj="null"
    
    if [[ "$enable_warp" =~ ^[yY] ]]; then
        local warp_port=$(read_input "WARP SOCKS5 ç«¯å£" "$DEFAULT_WARP_SOCKS_PORT")
        local warp_addr="$DEFAULT_WARP_SOCKS_ADDR"
        # æ„å»º Warp Outbound JSON ç‰‡æ®µ
        warp_obj=$(jq -n --arg addr "$warp_addr" --argjson port "$warp_port" '{protocol: "socks", tag: "warp", settings: {servers: [{address: $addr, port: $port}]}}')
        green "Warp åˆ†æµå·²é…ç½® ($warp_addr:$warp_port)"
    else
        yellow "Warp åˆ†æµæœªå¯ç”¨"
    fi
    
    # --- 4. æ ¸å¿ƒç”Ÿæˆé€»è¾‘ (ä½¿ç”¨ jq æ„å»ºå®Œæ•´é…ç½®) ---
    log "æ­£åœ¨ç”Ÿæˆé…ç½®æ–‡ä»¶..."
    
    # åŸºç¡€æ¡†æ¶
    local filter_base='
    {
        log: {loglevel: $log},
        inbounds: [
            {
                listen: (if $listen == "0.0.0.0" then null else $listen end),
                port: ($port | tonumber),
                sniffing: {enabled: true, destOverride: ["http", "tls", "quic"]},
                tag: ("inbound-" + $port)
            }
        ],
        outbounds: [
            {protocol: "freedom", tag: "direct"},
            {protocol: "blackhole", tag: "block"}
        ],
        routing: {
            domainStrategy: "IPIfNonMatch",
            rules: [
                {type: "field", outboundTag: "block", domain: ["geosite:category-ads-all"]},
                {type: "field", outboundTag: "direct", network: "tcp,udp"}
            ]
        }
    }
    '
    
    # åè®®æ³¨å…¥é€»è¾‘
    local filter_proto=""
    case "$type" in
        xray_reality)
            filter_proto='
            .inbounds[0].protocol = "vless" |
            .inbounds[0].settings = {
                clients: [{id: $uuid, flow: "xtls-rprx-vision"}],
                decryption: "none"
            } |
            .inbounds[0].streamSettings = {
                network: "tcp",
                security: "reality",
                realitySettings: {
                    show: false,
                    fingerprint: $fp,
                    target: $target,
                    serverNames: [$sni],
                    privateKey: $pk,
                    shortIds: [$sid]
                }
            }
            '
            ;;
        xray_mkcp)
            filter_proto='
            .inbounds[0].protocol = "vless" |
            .inbounds[0].settings = {
                clients: [{id: $uuid}],
                decryption: "none"
            } |
            .inbounds[0].streamSettings = {
                network: "kcp",
                kcpSettings: {
                    mtu: $mtu, tti: $tti, 
                    uplinkCapacity: $up, downlinkCapacity: $down, 
                    congestion: $cong, 
                    readBufferSize: $rb, writeBufferSize: $wb,
                    header: {type: $header},
                    seed: $seed
                }
            }
            '
            ;;
        xray_ss)
            filter_proto='
            .inbounds[0].protocol = "shadowsocks" |
            .inbounds[0].settings = {
                method: $method,
                password: $password,
                network: "tcp,udp"
            }
            '
            ;;
    esac
    
    # Warp æ³¨å…¥é€»è¾‘
    local filter_warp=""
    if [[ "$warp_obj" != "null" ]]; then
        jq_args+=( --argjson warp_outbound "$warp_obj" )
        # 1. æ’å…¥ warp outbound åˆ°ç´¢å¼• 1 (direct ä¹‹å)
        # 2. æ’å…¥ warp è·¯ç”±è§„åˆ™åˆ°å¼€å¤´
        filter_warp='
        .outbounds |= (.[:1] + [$warp_outbound] + .[1:]) |
        .routing.rules |= ([
            {type: "field", outboundTag: "warp", domain: ["geosite:google","geosite:openai","suffix:openai.com","suffix:chatgpt.com"]},
            {type: "field", outboundTag: "warp", domain: ["suffix:ip-api.com"]}
        ] + .)
        '
    fi
    
    # æ‰§è¡Œ jq
    jq -n "${jq_args[@]}" "$filter_base | $filter_proto | $filter_warp" > "$conf_path"
    
    if [[ $? -eq 0 ]]; then
        deploy_service_instance "ax-xray@${next_id}.service" "$conf_path" "$(cat $conf_path)" || return 1
        
        # ç”Ÿæˆåˆ†äº«é“¾æ¥
        echo
        case "$type" in
            xray_reality) green "VLESS+Reality é“¾æ¥: $(generate_xray_reality_link $next_id)";;
            xray_mkcp) green "VLESS+mKCP é“¾æ¥: $(generate_xray_mkcp_link $next_id)";;
            xray_ss) green "Shadowsocks é“¾æ¥: $(generate_xray_ss_link $next_id)";;
        esac
    else
        red "ä¸¥é‡é”™è¯¯ï¼šJSON é…ç½®æ–‡ä»¶ç”Ÿæˆå¤±è´¥ï¼"
        return 1
    fi
}
